import React from 'react';

import globalState from './GlobalState'


class CardSt extends React.Component{
 	constructor(props) {
    	super(props);
    	this.state = {
      	cardList: [],
      	cards: {},
    	}

	}

  	componentDidMount() {
   		fetch('http://localhost:8080/cardlist')
			.then(response => response.json())
			.then(data => this.setState({ cardList: data }));
  	}

  	handleInput(e) {
    	let number = e.target.value;
    	let card = e.target.name;
    	this.setState(prevState => {
    	  	let cards = {...prevState.cards};
    	  	cards[card] = number;
      	return {cards};
    })

    e.preventDefault();
  	}

  	render() {
    	const cardList = this.state.cardList.map (card =>
      		<div className='col-sm-3 input'>

        		<img src={card.imageLink} alt={card.idCard}/>
        		<input type="text"  onChange = {(e) => this.handleInput(e)} name={card.idCard}  className='inputForm'/>
      		</div>
    	);

	    	if (cardList) 
      	return (
        	<div className="container-fluid mainPage">
				<h1 className= 'pageName'> Card list </h1>
				<div className='list container'>
					<div className='row'>
          			{cardList}
          			</div>
          		</div>
        	</div>
      	)
  }
}

export default CardSt;
