import React from 'react';

import '../style/Game.css'

import {
  Link,
  NavLink,
  Switch,
  Redirect
} from "react-router-dom";
//import component
// import Header from '../components/Header'

class Game extends React.Component{
	constructor(props){
		super(props);
		this.state = {
			pNum  : ''
		}
	}

	handleChange(evt){
		const num = (evt.target.validity.valid) ? evt.target.value : this.state.pNum;
		this.setState(num)
	}

	render (){
		return (
			<div className='mainPage container-fluid'>
				<div className='main pagename'>
					<h2>Setup player number</h2>
				</div>
				<div className='container setup'>
					<form>

					</form>
				</div>
			</div>
		)
	}
}

export default Game
