import React from 'react';

import globalState from './GlobalState'


class CardSt extends React.Component{
 	constructor(props) {
    	super(props);
    	this.state = {
      	cardList: [],
      	cards: {},
    	}
    	this.findCard = this.findCard.bind(this);
	}

  	componentDidMount() {
  		// if (globalState.pNum === 0){
  		// 	let path = '/setup/player';
  		// 	this.props.history.push(path);
  		// }

   		fetch('http://localhost:8080/cardlist')
			.then(response => response.json())
			.then(data => this.setState({ cardList: data }));

  	}

  	handleInput(e) {
    	let number = e.target.value;
    	let card = e.target.name;
    	this.setState(prevState => {
    	  	let cards = {...prevState.cards};
    	  	cards[card] = number;
      	return {cards};
    })

    e.preventDefault();
  	}

  	handleSummit(e){
  		// this.state.cardList.forEach((card) => console.log(card.idCard));
  		let pList = [];
  		for (var i=0;i<globalState.pNum;i++) pList.push(i);
  		let chosenList = this.state.cards;
  		let chosen;

		// console.log(globalState.pNum + ' '+ globalState.players[globalState.pNum-1].name);

  		for (chosen in chosenList){
			for (let i=0;i<chosenList[chosen]; i++){
				var Pid = pList[Math.floor(Math.random()*pList.length)];

				for( var j = 0; j < pList.length; j++){ 
				   if ( pList[j] === Pid) {
				     pList.splice(j, 1); 
				   }
				}

				var player = globalState.players.find((element)=> {
					return element.id == Pid;
				});

				console.log(player.id + ' ' + player.name);
	  			let card = this.state.cardList[chosen];

	  			player.role = card;
				console.log(chosen +' '+ this.state.cardList[chosen].idCard+ ' ' + player.id );

				// player.role = card;
			}		
  		}
  		// globalState.players.forEach((P) => console.log(P.role.idCard));

    	e.preventDefault();
  	}

  	render() {
    	const cardList = this.state.cardList.map (card =>
      		<li key={card.idCard} className='col-sm-3 singleInput'>
      			<div className='image'>
	        		<img src={card.imageLink} alt={card.idCard}/>
      			</div>
		        <input type="text"  onChange = {(e) => this.handleInput(e)} name={card.idCard} />
      		</li>
    	);

	    	if (cardList) 
      	return (
        	<div className="container-fluid mainPage">
				<h1 className= 'pageName'> Card list </h1>
				<div className='container'>
					<form onSubmit={(e) => this.handleSummit(e)}>
						<ul className='row cardList'>
	          			{cardList}
	          			</ul>
	          			<input type="submit" value="Submit" />
	          		</form>
          		</div>
        	</div>
      	)
  }
}

export default CardSt;
