import React from 'react';
//style
import './Header.css';

// import page

import Main from '../view/Homepage';
import About from '../view/About';
import CardList from '../view/CardList';
import Rule from '../view/Rule';
import Game from '../view/Game';

//import router
import {
  BrowserRouter as Router,
  Route,
  Link,
  NavLink,
  Switch,
  Redirect
} from "react-router-dom";

class Header extends React.Component{
	render(){
		return (

			<div className='header'>
				<nav className='navbar navbar-expand-lg fixed-top ' id='mainNav'>
					<NavLink to='/' className = 'navbar-brand js-scroll-trigger text-uppercase'>Werewolf the Gamemaster</NavLink>

					<NavLink to='/game' className = 'nav-link py-3 px-0 px-lg-3 rounded js-scroll-trigger'>Play</NavLink>

					<button class="navbar-toggler navbar-toggler-right text-uppercase font-weight-bold bg-primary text-white rounded" type="button" 
					data-toggle="collapse" data-target="#navbarResponsive" aria-controls="navbarResponsive" aria-expanded="false" aria-label="Toggle navigation">
        				Menu
        				<i class="fas fa-bars"></i>
      				</button>

					<div className='collapse navbar-collapse justify-content-end' id="navbarResponsive">
						<ul className='navbar-nav ml-auto'>
						<NavLink to='/about' className = 'nav-link py-3 px-0 px-lg-3 rounded js-scroll-trigger'>About</NavLink>
						<NavLink to='/cl' className = 'nav-link py-3 px-0 px-lg-3 rounded js-scroll-trigger'>Card List</NavLink>
						<NavLink to='/rule' className = 'nav-link py-3 px-0 px-lg-3 rounded js-scroll-trigger'>Game Rule</NavLink>
						</ul>
					</div>



				</nav>
			</div>

		);
	}
}

export default Header;
