import React from 'react';

import '../style/Game.css'

import {
  BrowserRouter as Router,
  Route,
  Link,
  NavLink,
  Switch,
  Redirect
} from "react-router-dom";
//import component
// import Header from '../components/Header'

class Game extends React.Component{
	constructor(props){
		super(props);
		this.state = {
			pNum  : ''
		}
	}

	handleChange(evt){
		const num = (evt.target.validity.valid) ? evt.target.value : this.state.pNum;
		this.setState({num});	
	}

	render (){
		return (
			<div className='mainPage container-fluid'>
				<Route path='/setup/player' components={PlayerSt} />
				<Route path='/setup/card' components={CardSt} />
				<Route path='/setup/game' components={Werewolf} />
			</div>
		)
	}
}

class CardList extends React.Component{
	render (){
		return (
			<div>
				<h1>NAvi</h1>
			</div>
		)
	}
}

export default Game
